import React, { useState, useEffect } from 'react';
import './Cards.css';
import rightArrow from '../assets/right-arrow.png';
import leftArrow from '../asets/left-arrow.png';

const Cards = () => {
    const [isFlipped, setIsFlipped] = useState(false);

    const flashcards = [
        "What is the capital of France?",
        "What is the largest planet in our solar system?",
        "Who wrote 'Romeo and Juliet'?",
        "What is the chemical symbol for water?",
        "What is the powerhouse of the cell?"
    ];

    const [currentFlashcardIndex, setCurrentFlashcardIndex] = useState(0);

    useEffect(() => {
        const handleFlip = () => {
            setIsFlipped(!isFlipped);
        };

        const handleKeyPress = (event) => {
            if (event.keyCode === 37) { // Left arrow key
                setCurrentFlashcardIndex((prevIndex) => (prevIndex - 1 + flashcards.length) % flashcards.length);
            } else if (event.keyCode === 39) { // Right arrow key
                setCurrentFlashcardIndex((prevIndex) => (prevIndex + 1) % flashcards.length);
            } else if (event.keyCode === 32) { // Spacebar
                setIsFlipped(!isFlipped);
            }
        };

        document.addEventListener('keydown', handleKeyPress);

        return () => {
            document.removeEventListener('keydown', handleKeyPress);
        };
    }, []);

    return (
        <div className="flashcard">
            <h2>{flashcards[currentFlashcardIndex]}</h2>
        </div>
    );
};

export default Cards;
