import React, { useState } from "react";
import axios from "axios";
import Cards from "./Cards";

const Topics = ({ cardsContainer }) => {
    const [topicsList, setTopicsList] = useState([]);
    const [flashcardsData, setFlashcardsData] = useState([]);

    const selectedTopicsList = [];

    const selectTopic = (e) => {
        const topic = e.target.textContent;
        if (selectedTopicsList.includes(topic)) {
            // Deselect topic
            const index = selectedTopicsList.indexOf(topic);
            selectedTopicsList.splice(index, 1);
        } else {
            // Select topic
            selectedTopicsList.push(topic);
        }
    };

    const generateCards = async () => {
        const response = await axios.post(
            "http://localhost:3001/getflashcards",
            { topics: selectedTopicsList }
        );
        console.log(response.data);
        setFlashcardsData(response.data);
    };

    const generateQuiz = async () => {
        // Similar logic for generating quiz
    };

    const submitTopic = (e) => {
        if (e.key !== "Enter") return;
        const value = e.target.value.trim();
        if (!value) return;

        setTopicsList([...topicsList, value]);
        selectedTopicsList.push(e.target.value);
        e.target.value = '';
    };

    return (
        <div>
            <div className='popupBg'>
                <h1 className="title">Topics</h1>
                <div className="topics-list">
                    {topicsList.map((topic, index) => (
                        <div key={index} onClick={selectTopic}>
                            {topic}
                        </div>
                    ))}
                </div>
                <textarea onKeyPress={submitTopic} placeholder="Enter topic..." />
                <button className="generateButton" onClick={generateCards}>Generate</button>
            </div>
            {flashcardsData.length > 0 && (
                <Cards flashcards={flashcardsData} />
            )}
        </div>
    );
};

export default Topics;
