import React, { useState, useEffect } from 'react';
import './Cards.css';

const Cards = () => {
    const flashcards = [
        { question: "What is the capital of France?", answer: "Paris" },
        { question: "What is the largest planet in our solar system?", answer: "Jupiter" },
        { question: "Who wrote 'Romeo and Juliet'?", answer: "William Shakespeare" },
        { question: "What is the chemical symbol for water?", answer: "H2O" },
        { question: "What is the powerhouse of the cell?", answer: "Mitochondria" }
    ];

    const [currentFlashcardIndex, setCurrentFlashcardIndex] = useState(0);
    const [showAnswer, setShowAnswer] = useState(false);

    useEffect(() => {
        const handleKeyPress = (event) => {
            if (event.keyCode === 37) { // Left arrow key
                setCurrentFlashcardIndex((prevIndex) => (prevIndex - 1 + flashcards.length) % flashcards.length);
                setShowAnswer(false);
            } else if (event.keyCode === 39) { // Right arrow key
                setCurrentFlashcardIndex((prevIndex) => (prevIndex + 1) % flashcards.length);
                setShowAnswer(false);
            }
        };

        document.addEventListener('keydown', handleKeyPress);

        return () => {
            document.removeEventListener('keydown', handleKeyPress);
        };
    }, [flashcards.length]);

    let toggled = false
    const toggleAnswer = (e) => {
        if (!toggled)
            e.target.style.transform = 'rotate3d(0, 1, 0, 180deg)'
        else e.target.style.transform = ''
        setShowAnswer(!showAnswer);
    };

    return (
        <div className="flashcard" onClick={toggleAnswer}>
            <h2>{showAnswer ? flashcards[currentFlashcardIndex].answer : flashcards[currentFlashcardIndex].question}</h2>
        </div>
    );
};

export default Cards;
