import React, { useRef, useState } from "react";
import "./Topics.css";

const topicsList = ['Kinematics', 'Forces', 'Energy']
const selectedTopicsList = []


const selectTopic = (e) => {
    e.target.style.background = "#18A34F";
    selectedTopicsList.push(e.target.textContent)
}

const Topics = () => {
    return (<div>
        <div className='popupBg'>
            <h1 className="title">Topics</h1>
            <div className="topics-list">
                {topicsList.map((topic) => <div onClick={selectTopic}>{topic}</div>)}
            </div>
        </div>
    </div>)
}

export default Topics
